namespace Astro8.Devices;

public sealed class CharacterDevice<THandler> : MemoryDevice
    where THandler : Handler
{
    private const int FONT_WIDTH = 8;
    private const int FONT_HEIGHT = 8;
    private const int FONT_SIZE = FONT_WIDTH * FONT_HEIGHT;

    // No clue why, but it's being done here: https://github.com/sam-astro/Astro8-Computer/blob/0c5b1bc1f998ccc856bc99476546e427847b3bae/Astro8-Emulator/main.cpp#L642-L646
    private const int RENDER_WIDTH = 6;
    private const int RENDER_HEIGHT = 6;

    private readonly Screen<THandler>? _screen;
    private readonly bool _writeToConsole;
    private readonly int _width;
    private readonly int _height;
    private readonly int[] _values;
    private readonly bool[] _rom;

    public CharacterDevice(int address, Screen<THandler>? screen, string? data = null, bool writeToConsole = false)
        : base(address, (int)Math.Ceiling((double) (screen?.Width ?? 64) / RENDER_WIDTH * (screen?.Height ?? 64) / RENDER_WIDTH))
    {
        _screen = screen;
        _writeToConsole = writeToConsole;
        _width = (screen?.Width ?? 64) / RENDER_WIDTH;
        _height = (screen?.Height ?? 64) / RENDER_WIDTH;
        _values = new int[(_width + 1) * (_height + 1)];

        data ??= Default;

        var rom = new bool[Default.Length];

        for (var i = 0; i < Default.Length; i++)
        {
            rom[i] = data[i] == '1';
        }

        _rom = rom;
    }

    public override void Initialize(Span<int> span, bool isState)
    {
        for (var i = 0; i < span.Length; i++)
        {
            Write(i, span[i]);
        }
    }

    public override void Write(int address, int value)
    {
        if (_values[address] == value)
        {
            return;
        }

        _values[address] = value;

        var characterOffset = value * FONT_SIZE;

        var offsetX = address % _width;
        var offsetY = address / _width;

        if (_writeToConsole && Character.IntToChar.TryGetValue(value, out var c))
        {
            Console.SetCursorPosition(offsetX, offsetY);
            Console.Write(c);
        }

        if (_screen == null)
        {
            return;
        }

        var screenX = offsetX * RENDER_WIDTH;
        var screenY = offsetY * RENDER_HEIGHT;

        for (var i = 0; i < FONT_SIZE; i++)
        {
            var x = i % FONT_WIDTH;

            if (x >= RENDER_WIDTH)
            {
                continue;
            }

            var y = i / FONT_WIDTH;

            if (y >= RENDER_HEIGHT)
            {
                continue;
            }

            var offset = characterOffset + i;

            if (offset < 0 || offset >= _rom.Length)
            {
                continue;
            }

            var color = _rom[offset] ? ScreenColor.White : ScreenColor.Black;
            _screen.WriteOverlay(screenX + x + (screenY + y) * _screen.Width, color.Value);
        }
    }

    public const string Default = "";
}
